// This should set the decoder's source buffer, so the new decoding continues from
// the supplied column data. This should NOT reset the internal state of the decoder,
// since columns can cross frame boundaries and the new column data is considered
// continuation of that same column in the previous frame.
func (d *ScopeDecoder) Continue() {
	d.buf.Reset(d.column.Data())

	if d.fieldCount <= 0 {
		return // Name and subsequent fields are skipped.
	}
	d.nameDecoder.Continue()
	if d.fieldCount <= 1 {
		return // Version and subsequent fields are skipped.
	}
	d.versionDecoder.Continue()
	if d.fieldCount <= 2 {
		return // SchemaURL and subsequent fields are skipped.
	}
	d.schemaURLDecoder.Continue()
	if d.fieldCount <= 3 {
		return // Attributes and subsequent fields are skipped.
	}

	if !d.isAttributesRecursive {
		d.attributesDecoder.Continue()
	}

	if d.fieldCount <= 4 {
		return // DroppedAttributesCount and subsequent fields are skipped.
	}
	d.droppedAttributesCountDecoder.Continue()
}

func (d *ScopeDecoder) Reset() {

	if d.fieldCount <= 0 {
		return // Name and all subsequent fields are skipped.
	}
	d.nameDecoder.Reset()
	if d.fieldCount <= 1 {
		return // Version and all subsequent fields are skipped.
	}
	d.versionDecoder.Reset()
	if d.fieldCount <= 2 {
		return // SchemaURL and all subsequent fields are skipped.
	}
	d.schemaURLDecoder.Reset()
	if d.fieldCount <= 3 {
		return // Attributes and all subsequent fields are skipped.
	}

	if !d.isAttributesRecursive {
		d.attributesDecoder.Reset()
	}

	if d.fieldCount <= 4 {
		return // DroppedAttributesCount and all subsequent fields are skipped.
	}
	d.droppedAttributesCountDecoder.Reset()
}

func (d *ScopeDecoder) Decode(dstPtr **Scope) error {
	// Check if this is a dictionary-based decoding.
	dictFlag := d.buf.ReadBit()
	if dictFlag == 0 {
		refNum := d.buf.ReadUvarintCompact()
		if refNum >= uint64(len(d.dict.dict)) {
			return pkg.ErrInvalidRefNum
		}
		*dstPtr = d.dict.dict[refNum]
		return nil
	}

	// *dstPtr is pointing to a element in the dictionary. We are not allowed
	// to modify it. Make a clone of it and decode into the clone.
	val := (*dstPtr).Clone(d.allocators)
	*dstPtr = val

	var err error

	// Read bits that indicate which fields follow.
	val.modifiedFields.mask = d.buf.ReadBits(d.fieldCount)

	if val.modifiedFields.mask&fieldModifiedScopeName != 0 {
		// Field is changed and is present, decode it.
		err = d.nameDecoder.Decode(&val.name)
		if err != nil {
			return err
		}
	}

	if val.modifiedFields.mask&fieldModifiedScopeVersion != 0 {
		// Field is changed and is present, decode it.
		err = d.versionDecoder.Decode(&val.version)
		if err != nil {
			return err
		}
	}

	if val.modifiedFields.mask&fieldModifiedScopeSchemaURL != 0 {
		// Field is changed and is present, decode it.
		err = d.schemaURLDecoder.Decode(&val.schemaURL)
		if err != nil {
			return err
		}
	}

	if val.modifiedFields.mask&fieldModifiedScopeAttributes != 0 {
		// Field is changed and is present, decode it.
		err = d.attributesDecoder.Decode(&val.attributes)
		if err != nil {
			return err
		}
	}

	if val.modifiedFields.mask&fieldModifiedScopeDroppedAttributesCount != 0 {
		// Field is changed and is present, decode it.
		err = d.droppedAttributesCountDecoder.Decode(&val.droppedAttributesCount)
		if err != nil {
			return err
		}
	}

	d.dict.dict = append(d.dict.dict, val)
	// Freeze the value. It is now in the dictionary and must not be modified.
	// This also improves performance of any encode operations that use this
	// value as it can be safely shared in encoder's dictionary without cloning.
	val.Freeze()

	return nil
}

// ScopeDecoderDict is the dictionary used by ScopeDecoder
type ScopeDecoderDict struct {
	dict []*Scope
}

func (d *ScopeDecoderDict) Init() {
	d.dict = d.dict[:0]
	d.dict = append(d.dict, nil) // nil Scope is RefNum 0
}

// Reset the dictionary to initial state. Used when a frame is
// started with RestartDictionaries flag.
func (d *ScopeDecoderDict) Reset() {
	d.Init()
}

// ScopeAllocator implements a custom allocator for Scope.
// It maintains a pool of pre-allocated Scope and grows the pool
// dynamically as needed, up to a maximum size of 64 elements.
type ScopeAllocator struct {
	pool []Scope
	ofs  int
}

// Alloc returns the next available Scope from the pool.
// If the pool is exhausted, it grows the pool by doubling its size
// up to a maximum of 64 elements.
func (a *ScopeAllocator) Alloc() *Scope {
	if a.ofs < len(a.pool) {
		// Get the next available Scope from the pool
		a.ofs++
		return &a.pool[a.ofs-1]
	}
	// We've exhausted the current pool, prealloc a new pool.
	return a.prealloc()
}

//go:noinline
func (a *ScopeAllocator) prealloc() *Scope {
	// prealloc expands the pool by doubling its size, up to a maximum of 64 elements.
	// If the pool is empty, it starts with 1 element.
	newLen := min(max(len(a.pool)*2, 1), 64)
	a.pool = make([]Scope, newLen)
	a.ofs = 1
	return &a.pool[0]
}
